$(document).ready(function(){$(window).resize(function(){var topHeader=$("#top-header");if($(window).width()<=650){if(topHeader.hasClass("fixed")){topHeader.removeClass("fixed");}if(!topHeader.hasClass("absolute")){topHeader.addClass("absolute");}}else{if(!topHeader.hasClass("fixed")){topHeader.addClass("fixed");}if(topHeader.hasClass("absolute")){topHeader.removeClass("absolute");}}});$("#navbar-mobile-btn").click(function(){$("#navbar").toggleClass("toggle");});$(document).mouseup(function(e){var container=$("#navbar");var btn=$("#navbar-mobile-btn");if(!container.is(e.target)&&container.has(e.target).length===0){if(container.hasClass("toggle")){container.removeClass("toggle");}}});$("#submenu-mobile-btn").click(function(){$("#submenu").toggleClass("toggle");});$(document).mouseup(function(e){var container=$("#submenu");if(!container.is(e.target)&&container.has(e.target).length===0){if(container.hasClass("toggle")){container.removeClass("toggle");}}});$(".submenu-btn").click(function(e){$("html, body").animate({scrollTop:$($.attr(this,"href")).offset().top-80},200);return false;});});